var auth = require('./index.js');

auth.mysql({
	host: "localhost",
	user: "root",
	password: "mysql",
	database: "codeauth",
	table: "codes"
}, function() {
	var userCode = new auth.code( "techpanda@digitalpanda.ca", 6 );
	userCode.store();
	setTimeout(function(){
		auth.check( userCode.salt, userCode.code, function( result ){
			if (result) console.log("It works!")
			auth.close();
		});
	},5000);
});